mapper.select:
select tr.*
from t_admin_resource tar
join t_resource tr on tar.resource_id = tr.id
where tar.admin_id = #{adminId}

    and tr.status = #{state}

union
select tre.*
from t_admin_role tar
join t_role tr on tar.role_id = tr.id

    and tr.status = #{state}

join t_role_resource trr on tr.id = trr.role_id
join t_resource tre on trr.resource_id = tre.id
where tar.admin_id = #{adminId}

    and tre.status = #{state}
